  }

  // è§£æå¤‡ç”¨æœåŠ¡å™¨åˆ—è¡¨
  parseBackupServers(backupServersString) {
    if (!backupServersString) return [];

    return backupServersString
      .split('\n')
      .map(server => server.trim())
      .filter(server => server && server.startsWith('http'));
  }

  // è·å–åç«¯APIåŸºç¡€URL
  getBackendApiUrl() {
    // åœ¨å¼€å‘ç¯å¢ƒä¸‹ï¼Œä½¿ç”¨ä»£ç†
    if (import.meta.env.DEV) {
      return '';  // ä½¿ç”¨ç›¸å¯¹è·¯å¾„ï¼Œç”±Viteä»£ç†å¤„ç†
    }

    // åœ¨ç”Ÿäº§ç¯å¢ƒä¸‹ï¼Œä½¿ç”¨ç¯å¢ƒå˜é‡æˆ–é»˜è®¤å€¼
    return import.meta.env.VITE_API_BASE_URL || window.location.origin;
  }

  // è·å–é…ç½®ï¼ˆå¼‚æ­¥ï¼‰
  async getConfig() {
