/* 移动端优质模板样式 */

/* 确保CSS变量兼容性 */
:root {
  /* 兼容原有变量系统 */
  --van-primary-color: var(--primary-color, #1989fa);
  --van-success-color: var(--success-color, #07c160);
  --van-warning-color: var(--warning-color, #ff976a);
  --van-danger-color: var(--error-color, #ee0a24);

  /* 背景色兼容 */
  --van-background-color: var(--bg-primary, #000000);
  --van-background-color-light: var(--bg-card, #1a1a1a);
  --van-background-2: var(--bg-secondary, #2d2d2d);

  /* 文字色兼容 */
  --van-text-color: var(--text-color, #f7f8fa);
  --van-text-color-2: var(--text-light, #969799);
  --van-text-color-3: var(--text-muted, #646566);

  /* 边框色兼容 */
  --van-border-color: var(--border-color, #323233);
}

/* 移动端容器 */
.mobile-container {
  max-width: 100%;
  margin: 0 auto;
  background: var(--van-background-color);
  min-height: 100vh;
  position: relative;
  overflow-x: hidden;
}

/* 移动端安全区域 */
.mobile-safe-area {
  padding-top: env(safe-area-inset-top);
  padding-bottom: env(safe-area-inset-bottom);
  padding-left: env(safe-area-inset-left);
  padding-right: env(safe-area-inset-right);
}

/* 移动端头部 */
.mobile-header {
  position: sticky;
  top: 0;
  z-index: 100;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  border-bottom: 1px solid var(--van-border-color);
  padding: 12px 16px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  min-height: 56px;
}

.mobile-header-title {
  font-size: 18px;
  font-weight: 600;
  color: var(--van-text-color);
  margin: 0;
}

.mobile-header-actions {
  display: flex;
  align-items: center;
  gap: 8px;
}

/* 移动端内容区域 */
.mobile-content {
  padding: 16px;
  padding-bottom: 80px; /* 为底部导航留空间 */
  min-height: calc(100vh - 56px);
}

/* 移动端卡片 */
.mobile-card {
  background: var(--van-background-color-light);
  border-radius: 16px;
  padding: 20px;
  margin-bottom: 16px;
  border: 1px solid var(--van-border-color);
  box-shadow: 0 2px 12px rgba(0, 0, 0, 0.08);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.mobile-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.12);
}

.mobile-card-header {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 16px;
}

.mobile-card-icon {
  width: 40px;
  height: 40px;
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, var(--van-primary-color), #667eea);
  color: white;
  font-size: 20px;
}

.mobile-card-title {
  font-size: 16px;
  font-weight: 600;
  color: var(--van-text-color);
  margin: 0;
}

.mobile-card-subtitle {
  font-size: 14px;
  color: var(--van-text-color-2);
  margin: 4px 0 0 0;
}

.mobile-card-content {
  color: var(--van-text-color-2);
  line-height: 1.6;
}

/* 移动端网格布局 */
.mobile-grid {
  display: grid;
  gap: 16px;
}

.mobile-grid-2 {
  grid-template-columns: repeat(2, 1fr);
}

.mobile-grid-3 {
  grid-template-columns: repeat(3, 1fr);
}

/* 移动端列表 */
.mobile-list {
  background: var(--van-background-color-light);
  border-radius: 16px;
  overflow: hidden;
  border: 1px solid var(--van-border-color);
}

.mobile-list-item {
  padding: 16px;
  border-bottom: 1px solid var(--van-border-color);
  display: flex;
  align-items: center;
  gap: 12px;
  transition: background-color 0.2s;
}

.mobile-list-item:last-child {
  border-bottom: none;
}

.mobile-list-item:active {
  background: var(--van-active-color);
}

.mobile-list-icon {
  width: 32px;
  height: 32px;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--van-primary-color);
  color: white;
  font-size: 16px;
  flex-shrink: 0;
}

.mobile-list-content {
  flex: 1;
}

.mobile-list-title {
  font-size: 16px;
  font-weight: 500;
  color: var(--van-text-color);
  margin: 0 0 4px 0;
}

.mobile-list-desc {
  font-size: 14px;
  color: var(--van-text-color-2);
  margin: 0;
}

.mobile-list-arrow {
  color: var(--van-text-color-3);
  font-size: 16px;
}

/* 移动端底部导航 */
.mobile-tabbar {
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  border-top: 1px solid var(--van-border-color);
  padding: 8px 0;
  padding-bottom: calc(8px + env(safe-area-inset-bottom));
  z-index: 1000;
}

.mobile-tabbar-content {
  display: flex;
  justify-content: space-around;
  align-items: center;
  max-width: 500px;
  margin: 0 auto;
  padding: 0 16px;
}

.mobile-tabbar-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
  padding: 8px 12px;
  border-radius: 12px;
  transition: all 0.2s;
  cursor: pointer;
  min-width: 60px;
}

.mobile-tabbar-item.active {
  background: rgba(25, 137, 250, 0.1);
  color: var(--van-primary-color);
}

.mobile-tabbar-icon {
  font-size: 20px;
  transition: transform 0.2s;
}

.mobile-tabbar-item.active .mobile-tabbar-icon {
  transform: scale(1.1);
}

.mobile-tabbar-label {
  font-size: 12px;
  font-weight: 500;
  color: inherit;
}

/* 移动端状态栏 */
.mobile-status-bar {
  background: var(--van-primary-color);
  color: white;
  padding: 8px 16px;
  font-size: 14px;
  text-align: center;
  position: relative;
}

.mobile-status-bar.success {
  background: var(--van-success-color);
}

.mobile-status-bar.warning {
  background: var(--van-warning-color);
}

.mobile-status-bar.error {
  background: var(--van-danger-color);
}

/* 移动端浮动按钮 */
.mobile-fab {
  position: fixed;
  bottom: 80px;
  right: 16px;
  width: 56px;
  height: 56px;
  border-radius: 50%;
  background: var(--van-primary-color);
  color: white;
  border: none;
  box-shadow: 0 4px 16px rgba(25, 137, 250, 0.3);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  z-index: 999;
}

.mobile-fab:hover {
  transform: scale(1.1);
  box-shadow: 0 6px 20px rgba(25, 137, 250, 0.4);
}

.mobile-fab:active {
  transform: scale(0.95);
}

/* 移动端加载状态 */
.mobile-loading {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 40px 20px;
  color: var(--van-text-color-2);
}

.mobile-loading-spinner {
  margin-bottom: 16px;
}

.mobile-loading-text {
  font-size: 14px;
}

/* 移动端空状态 */
.mobile-empty {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 60px 20px;
  color: var(--van-text-color-2);
  text-align: center;
}

.mobile-empty-icon {
  font-size: 48px;
  margin-bottom: 16px;
  opacity: 0.5;
}

.mobile-empty-text {
  font-size: 16px;
  margin-bottom: 8px;
}

.mobile-empty-desc {
  font-size: 14px;
  opacity: 0.7;
}

/* 响应式断点 */
@media (max-width: 480px) {
  .mobile-content {
    padding: 12px;
  }

  .mobile-card {
    padding: 16px;
    margin-bottom: 12px;
  }

  .mobile-grid {
    gap: 12px;
  }

  .mobile-grid-3 {
    grid-template-columns: repeat(2, 1fr);
  }
}

/* 暗黑主题适配 */
@media (prefers-color-scheme: dark) {
  .mobile-header {
    background: rgba(26, 26, 26, 0.95);
  }

  .mobile-tabbar {
    background: rgba(26, 26, 26, 0.95);
  }
}
