  console.log('üß™ ÊµãËØïÁôªÂΩïAPI...');

  const postData = JSON.stringify({
    username: 'admin',
    password: 'admin123456'
  });

  const options = {
    hostname: 'localhost',
    port: 3007,
    path: '/api/auth/login',
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Content-Length': Buffer.byteLength(postData)
    }
