  const req = http.request(options, (res) => {
    let data = '';

    console.log(`üìä Áä∂ÊÄÅÁ†Å: ${res.statusCode}`);
    console.log(`üìä ÂìçÂ∫îÂ§¥:`, res.headers);

    res.on('data', (chunk) => {
      data += chunk;
    });

    res.on('end', () => {
      console.log('üìä ÂìçÂ∫îÊï∞ÊçÆ:', data);

      try {
        const jsonData = JSON.parse(data);
        console.log('‚úÖ JSONËß£ÊûêÊàêÂäü:', JSON.stringify(jsonData, null, 2));
      } catch (error) {
        console.log('‚ùå JSONËß£ÊûêÂ§±Ë¥•:', error.message);
        console.log('ÂéüÂßãÂìçÂ∫î:', data);
      }
    });
  });

  req.on('error', (error) => {
    console.log(`‚ùå ËØ∑Ê±ÇÂ§±Ë¥•: ${error.message}`);
  });

  req.write(postData);
  req.end();
};

testLoginAPI();
